import React, { useState } from "react";

export default function ServicesSection() {
  const [showFullProposal, setShowFullProposal] = useState(false);

  const services = [
    {
      title: "Community & Agricultural Support",
      description:
        "Delivered poultry feed (hens layer mash) under a six-month contract for The Church of Jesus Christ of Latter-day Saints, Ghana University Stake, Bomso. We provided house-to-house delivery of the feed to participants for free. Members testified of the quality, including Brother Bright Asadu, who compared our feed to a more expensive alternative and reported drastically lower output with the other supplier.",
    },
    {
      title: "IT Infrastructure & Data Center Deployment",
      description:
        "Prepared deployment proposals for PromisedLandâ€™s new data center in Independence, Missouri. Designed server roles, disk partitioning, user groups, shared storage, and security measures to support hybrid call centers across the U.S. and Canada. Integrated Azure and AWS services for scalability and redundancy.",
    },
    {
      title: "Professional Branding & Documentation",
      description:
        "Crafted clear, contract-ready documentation and proposals for enterprises and community projects, ensuring persuasive communication and reliable delivery.",
    },
  ];

  return (
    <section
      id="services"
      className="py-16 md:py-20 lg:py-24 bg-[#FAFAF6] dark:bg-[#1E1E1E]"
    >
      <div className="max-w-7xl mx-auto px-6 lg:px-12">
        <h2
          className="mb-12 font-semibold text-[#111] dark:text-white/90"
          style={{
            fontFamily: '"Montserrat", sans-serif',
            fontSize: "clamp(28px, 3vw, 36px)",
            lineHeight: "1.25",
          }}
        >
          Our Services & Projects
        </h2>

        <div className="grid md:grid-cols-2 gap-8">
          {services.map((service, index) => (
            <div
              key={index}
              className="p-6 rounded-lg bg-white dark:bg-[#121212] shadow hover:shadow-lg transition-shadow duration-200"
            >
              <h3
                className="mb-4 font-medium text-[#2563eb] dark:text-[#3b82f6]"
                style={{
                  fontFamily: '"Montserrat", sans-serif',
                  fontSize: "20px",
                }}
              >
                {service.title}
              </h3>
              <p
                className="text-[#444] dark:text-white/70"
                style={{
                  fontFamily: '"Rethink Sans", sans-serif',
                  fontSize: "15px",
                  lineHeight: "1.6",
                }}
              >
                {service.description}
              </p>
            </div>
          ))}
        </div>

        {/* Expandable Full Proposal Section */}
        <div className="mt-12">
          <button
            onClick={() => setShowFullProposal(!showFullProposal)}
            className="px-4 py-2 rounded-lg bg-[#2563eb] text-white hover:bg-[#1d4ed8] transition"
          >
            {showFullProposal ? "Hide Full Technical Steps" : "Read Full Technical Steps"}
          </button>

          {showFullProposal && (
            <div
              className="mt-6 p-4 rounded-lg bg-[#FAFAF6] dark:bg-[#1E1E1E] border border-gray-200 dark:border-gray-700"
              style={{
                fontFamily: '"Rethink Sans", sans-serif',
                fontSize: "14px",
                lineHeight: "1.6",
              }}
            >
              <h4 className="font-semibold mb-2 text-[#111] dark:text-white/90">
                PromisedLand Data Center Deployment (Technical Excerpt)
              </h4>
              <p className="mb-2">
                â€¢ Active Directory Domain Services, DNS, DHCP, Hyper-V, Storage Spaces Direct  
                â€¢ Microsoft Azure integration (IaaS, PaaS, Hybrid Backup & Recovery)  
                â€¢ IPAM for centralized IP address management  
                â€¢ NTFS permissions and ACLs for secure file access  
                â€¢ Intel Xeon processors, 16 GB RAM, 1 TB storage  
              </p>
              <p className="mb-2">
                â€¢ DNSSEC, forward/reverse lookup zones, DNS forwarding  
                â€¢ Group Policy Objects for password policies, audit policies, security settings  
                â€¢ System Center Operations Manager + Azure Monitor for hybrid monitoring  
                â€¢ Storage Spaces Direct with deduplication and compression  
                â€¢ Azure Backup + System Center Data Protection Manager for disaster recovery  
              </p>
              <p>
                ðŸ‘‰ For the full technical document, please refer to the detailed
                proposal prepared by Edward Wonder Mordey (October 2024).
              </p>
            </div>
          )}
        </div>
      </div>s
    </section>
  );
}
